use lazy_static::lazy_static;use log::debug;use rbatis::Rbatis;use crate::config::profile_config::CONFIG;use tokio::sync::Mutex;lazy_static!{    pub static ref PG: Mutex<Rbatis> = Mutex::new(        init()    );}pub fn init() -> Rbatis {    let rb = Rbatis::new();    rb.init(        rbdc_pg::driver::PgDriver{},        &CONFIG.pgsql_address    ).expect("pgsql存储失败！");    debug!("pgsql初始化完毕");    rb}